pragma solidity ^0.4.18;

import "./ERCReceivingContract.sol";
import "./Token.sol";
import "./TokenFromScratch3.sol";
import "./ERCReceivingContract.sol";

contract CrowdSale is ERCReceivingContract { //need ERC223 to notify when the contract recieves Tokens

  Token private _token;
  uint private _start;
  uint private _end;
  uint private _price;
  uint private _limit;
  uint private _balance;

  mapping(address => uint) private _limits;

  modifier withinLimit(address recipient, uint value) {
    uint amount = value/_price;
    require(_limits[recipient]+amount < _limit );
    _;
  }

  modifier isAvailable() {
    require(block.number >= _start && block.number < _end);
    require(_balance > 0);
    _;
  }

  event Buy(address beneficiary , uint amount);

  function CrowdSale(address token, uint start, uint end, uint price, uint limit) public {
      _token = Token(token);
      _start = start;
      _end = end;
      _price = price;
      _limit = limit;
  }

  function() external payable {
    revert();
  }


  function availableBalance() external view returns(uint) {
      return _balance;
  }

  function buy() public payable {
    buyFor(msg.sender);

  }

  function buyFor(address beneficiary) public isAvailable() withinLimit(beneficiary, msg.value) payable {
    uint amount = msg.value/_price;
    _token.transfer(beneficiary,amount);
    _limits[beneficiary] += amount;
    _balance -= amount;
    emit Buy(beneficiary, amount);
  }

  function tokenFallback(address _from, uint _value, bytes _data) external {
      _balance += _value;
  }

}
